   public double[] getLocation() {
      double percent = getPeriodPercent();
      System.out.printf("Period percent: %.3f\n", percent);
      boolean reverse;

      if ((percent < 10) ||
              (percent >= 20 && percent < 30) ||
              (percent >= 40 && percent < 50) ||
              (percent >= 60 && percent < 70) ||
              (percent >= 80 && percent < 90)) {
         reverse = false;
      } else {
         reverse = true;
      }
      if (reverse)
         System.out.println("Moving in reverse!");
      else
         System.out.println("Moving forward!");
      percent = percent%10 / 10;
      return getRouteCoordinate(route, percent, reverse);
   }
